mapper.select:
select a.*, b.project_name from sys_role a left join sys_project b on a.project_id = b.project_id
a.project_id = #{e.projectId}
and a.role_name like '%'+#{e.roleName}+'%'
and a.role_key like '%'+#{e.roleKey}+'%'
and a.status = #{e.status}
order by role_sort

select distinct u.user_id, u.dept_id, u.username, u.telephone,u.sex, u.status, u.create_time from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where r.role_id = #{e.roleId}
AND u.username like concat('%', #{e.username}, '%')
AND u.telephone like concat('%', #{e.telephone}, '%')

select a.*
from sys_role a
         left join sys_user_role b
                   on a.role_id = b.role_id
where b.user_id = #{userId}
  and a.project_id = #{projectId}
